	double dx = (b.x - a.x) / N, dy = (b.y - a.y) / N;
	for (int i = 0; i <= N; i++)
	{
		e[i].x = a.x + dx * i, e[i].y = a.y + dy * i;
		t1[i] = dis(a, e[i]) / p;
	}
	dx = (d.x - c.x) / N, dy = (d.y - c.y) / N;
	for (int i = 0; i <= N; i++)
	{
		f[i].x = c.x + dx * i, f[i].y = c.y + dy * i;
		t2[i] = dis(d, f[i]) / q;
	}
  //外层三分循环，枚举AB段的情况，再结合sol函数，最终求出时间最小的情况！
	int z1 = 0, y1 = N;
