	{
		int mid1 = z1 + (y1 - z1) / 3;
		int mid2 = y1 - (y1 - z1) / 3;
		if (sol(mid1) <= sol(mid2)) y1 = mid2 - 1;
		else z1 = mid1 + 1;
	}
