```c
#include <stdio.h>

typedef long long ll;

int main() {
    ll n;
    scanf("%lld", &n);
    while (n % 2 == 0) n /= 2;
    ll ans = 0;
    for (ll i = 1; i * i <= n; ++i) {
        if (n % i == 0) {
            ans++;
            if (i * i != n) ans++;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
