        return 0
    elif x & 1:
        if(mem[x - 2] != -1):
            mem[x] = x + mem[x - 2]
            return x + mem[x - 2]
        return x + func(x - 2)
    else:
        if(mem[x - 2] != -1):
            mem[x] = mem[x - 2] - x
            return mem[x - 2] - x
        return func(x - 2) - x

