    {
        amount = 10000.00;
        rate = 0.0330;
        year = 1;
    }
    Fixed_Deposit(double a, double r, int y)
    {
        amount = a;
        rate = r;
        year = y;
    }
    double amount, rate;
    int year;
    void Show()
    {
        double total = amount * rate * year + amount;
