    {
        amount = a;
        rate = r;
        year = y;
    }
    double GetAmount()
    {
        return amount;
    }
    double GetRate()
    {
        return rate;
    }
    int GetYears()
    {
        return year;
    }
    double GetAll()
    {
        double total = amount * rate * year + amount;
        return total;
    }
};

