```c++
#include<iostream>
#include<cstring>
using namespace std;

//你提交的代码在这里
class CSTRING {
private:
    char* p = nullptr;
public:
    ~CSTRING() {
        if (p != nullptr) {
            delete[] p;
        }
    }
    friend ostream& operator<< (ostream& cout, CSTRING& c);
    friend istream& operator>> (istream& cin, CSTRING& c);
    CSTRING& operator=(CSTRING& c) {
        if (this == &c) return *this;
        delete[] p;
        if (c.p != nullptr) {
            p = new char[strlen(c.p) + 1];
            strcpy(p, c.p);
        } else {
            p = nullptr;
        }
        return *this;
    }
    CSTRING& operator+= (CSTRING& c) {
        if (c.p == nullptr) {
            return *this;
        }
        if (p == nullptr) {
            *this = c;
            return *this;
        }
        char* tmp = new char[strlen(c.p) + strlen(p) + 1];
        strcpy(tmp, p);
        strcat(tmp, c.p);
        delete[] p;
        p = tmp;
        return *this;
    }
};
istream& operator>> (istream& cin, CSTRING& c) {
    string tmp;
    cin >> tmp;
    delete[] c.p;
    c.p = new char [tmp.length() + 1];
    strcpy(c.p, tmp.c_str());
    return cin;
}
ostream& operator<< (ostream& cout, CSTRING& c) {
    cout << c.p;
    return cout;
}

int main()
{
    int n,i;
    cin>>n;
    CSTRING t,*cs=new CSTRING[n];
    for(i=0;i<n;i++)
