#include<iomanip>
using namespace std;

//你提交的代码在这里
class Person {
public:
    string name = "NULL";
    int age = 0;
    bool operator> (Person p) {
        if (age != p.age) {
            return age > p.age;
        }
        return name > p.name;
    }
    Person& operator= (Person p) {
        name = p.name;
        age = p.age;
        return *this;
    }
};
istream& operator>> (istream& cin, Person& s) {
    cin >> s.name >> s.age;
    return cin;
}
ostream& operator<< (ostream& cout, Person& s) {
    cout << "name=" << s.name << ",age=" << s.age;
    return cout;
}

int main()
{
    int n,i,j;
    Person t;
    cin>>n;
    Person *person=new Person[n];
    for(i=0;i<n;i++)
        cin>>person[i];
    for(i=0;i<n-1;i++)
        for(j=0;j<n-i-1;j++)
            if(person[j]>person[j+1])
            { t=person[j];   person[j]=person[j+1];   person[j+1]=t;   }
    for(i=0;i<n;i++)
        cout<<"("<<i+1<<")"<<person[i]<<endl;
    delete[] person;
    return 0;
}
