    void Show()
    {
        if (real == 0 && imag != 0) cout << imag << "i" << endl;
        else if (imag == 0) cout << real << endl;
        else
        {
            if (imag > 0) cout << real << "+" << imag << "i" << endl;
            else cout << real << imag << "i" << endl;
        }
    }
