    }
    void Set(double sxx, double syy, double exx, double eyy)
    {
        sx = sxx, sy = syy;
        ex = exx, ey = eyy;
    }
    void Set()
    {
        sx = 1, sy = 5;
        ex = 8, ey = 4;
    }
};

int main()
