### c++.2001 时间类运算符重载

```c++
#include<iostream>
using namespace std;

//你提交的代码在这里
class Time
{
public:
    Time()
    {
        hour = 1;
        minute = 2;
        second = 3;
    }
    Time(int h, int m, int s)
    {
        hour = h;
        minute = m;
        second = s;
      	normalize();
    }
    int hour, minute, second;
    void Set(int hh, int mm, int ss)
    {
        hour = hh;
        minute = mm;
        second = ss;
        normalize();
    }
    void normalize()
    {
        minute += second / 60;
        second %= 60;
        if (second < 0)
        {
            second += 60;
            minute--;
        }
        hour += minute / 60;
        minute %= 60;
        if (minute < 0)
        {
            minute += 60;
            hour--;
        }
        while (hour < 0)
        {
            hour += 24;
        }
        hour %= 24;
    }
};
ostream& operator<< (ostream& cout, Time& t)
{
    cout << t.hour << ":" << t.minute << ":" << t.second;
    return cout;
}
long long operator- (Time& t1, Time& t2)
{
    long long s1 = t1.hour * 3600 + t1.minute * 60 + t1.second;
    long long s2 = t2.hour * 3600 + t2.minute * 60 + t2.second;
    return (s1 - s2);
}
Time operator+ (Time& t1, int s)
{
    Time t2 = t1;
    t2.second += s;
    t2.normalize();
    return t2;
}
Time operator- (Time& t1, int s)
{
    return t1 + (-s);
}

int main()
{
    Time t1;
    cout<<"Show object t1:"<<t1<<endl;
    int hour,minute,second;
    cin>>hour>>minute>>second;
    Time t2(hour,minute,second);
    cout<<"Show object t2:"<<t2<<endl;
    cin>>hour>>minute>>second;
    t1.Set(hour,minute,second);
    cout<<"Reset and Show object t1:"<<t1<<endl;
    int n;
    n=t2-t1;
    cout<<t2<<"-"<<t1<<"="<<n<<"s"<<endl;
    cin>>n;
    cout<<t1<<"+"<<n<<"s=";
    t2=t1+n;
    cout<<t2<<endl;
    cout<<t1<<"-"<<n<<"s=";
    t1=t1-n;
    cout<<t1<<endl;
    return 0;
}
