### c++.2002 日期类运算符重载

```c++
#include<iostream>
using namespace std;

//你提交的代码在这里
class Date
{
public:
    Date()
    {
        year = 2023;
        month = 5;
        day = 20;
    }
    Date(int y, int m, int d)
    {
        year = y;
        month = m;
        day = d;
        normalize();
    }
    int year, month, day;
    int mp[13] = {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
    int mr[13] = {0, 31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
    bool isr(int y)
    {
        return (y % 400 == 0) || (y % 100 != 0 && y % 4 == 0);
    }
    long long tianshu()
    {
        long long num = 0;
        for (int y = 1; y < year; y++)
        {
            num += isr(y) ? 366 : 365;
        }
        for (int m = 1; m < month; m++)
        {
            if (isr(year)) num += mr[m];
            else num += mp[m];
        }
        num += day;
        return num;
    }
    void normalize()
    {
        while (day > (isr(year) ? mr[month] : mp[month]))
        {
            day -= (isr(year) ? mr[month] : mp[month]);
            month++;
            if (month > 12)
            {
                month = 1;
                year++;
            }
        }
        while (day < 1)
        {
            month--;
            if (month < 1)
            {
                month = 12;
                year--;
            }
            day += (isr(year) ? mr[month] : mp[month]);
        }
    }
    void Set(int y, int m, int d)
    {
        year = y;
        month = m;
        day = d;
        normalize();
    }
    long long operator- (Date& d)
    {
        return tianshu() - d.tianshu();
    }
    Date operator+ (int n)
    {
        Date tmp = *this;
        tmp.day += n;
        tmp.normalize();
        return tmp;
    }
    Date operator- (int n)
    {
        return *this + (-n);
    }
};
ostream& operator<< (ostream& cout, Date& d)
{
    cout << d.year << "/" << d.month << "/" << d.day;
    return cout;
}

int main()
{
    Date d1;
    cout<<"Show object d1:"<<d1<<endl;
    int year,month,day;
    cin>>year>>month>>day;
    Date d2(year,month,day);
    cout<<"Show object d2:"<<d2<<endl;
    cin>>year>>month>>day;
    d1.Set(year,month,day);
    cout<<"Reset and Show object d1:"<<d1<<endl;
    int n;
    n=d2-d1;
    cout<<d2<<" - "<<d1<<" = "<<n<<endl;
    cin>>n;
    cout<<d1<<" + "<<n<<" = ";
    d2=d1+n;
    cout<<d2<<endl;
    cout<<d1<<" - "<<n<<" = ";
    d1=d1-n;
    cout<<d1<<endl;
    return 0;
}
```

### c++.2003 复数类运算符重载

```c++
#include<iostream>
using namespace std;

//你提交的代码在这里
class Complex
{
public:
    Complex()
    {
        real = 0;
        imag = 0;
    }
    Complex(double r, double i)
    {
        real = r;
        imag = i;
    }
    double real, imag;
    void Set(double r, double i)
    {
        real = r;
        imag = i;
    }
    Complex operator+ (Complex c)
    {
        Complex tmp = *this;
        tmp.real += c.real;
        tmp.imag += c.imag;
        return tmp;
    }
    Complex operator- (Complex c)
    {
        Complex tmp = *this;
        tmp.real -= c.real;
        tmp.imag -= c.imag;
        return tmp;
    }
    Complex operator* (Complex c)
    {
        Complex tmp;
        tmp.real = this->real * c.real - this->imag * c.imag;
        tmp.imag = this->real * c.imag + this->imag * c.real;
        return tmp;
    }
};
ostream& operator<< (ostream& cout, Complex& c)
{
    if (c.imag == 0) cout << c.real;
    else if (c.real == 0 && c.imag == 1) cout << "i";
    else if (c.real == 0 && c.imag == -1) cout << "-i";
    else if (c.real == 0) cout << c.imag << "i";
    else if (c.imag == 1) cout << c.real << "+i";
    else if (c.imag == -1) cout << c.real << "-i";
    else
    {
        if (c.imag > 0) cout << c.real << "+" << c.imag << "i";
        else cout << c.real << c.imag << "i";
    }
    return cout;
}

int main()
{
    Complex c1;
    cout<<"Show object c1:"<<c1<<endl;
    double re,im;
    cin>>re>>im;
    Complex c2(re,im);
    cout<<"Show object c2:"<<c2<<endl;
    cin>>re>>im;
    c1.Set(re,im);
    cout<<"Reset and Show object c1:"<<c1<<endl;
    Complex c3;
    c3=c1+c2;
    cout<<c1<<" + "<<c2<<" = "<<c3<<endl;
    c3=c1-c2;
    cout<<c1<<" - "<<c2<<" = "<<c3<<endl;
    c3=c1*c2;
    cout<<c1<<" * "<<c2<<" = "<<c3<<endl;
    return 0;
}
