这里的题干。。。。。。。。。

```c++
#include<iostream>
using namespace std;

//你提交的代码在这里
class Date {
public:
    int year, month, day;
    int mp[13] = {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
    int mr[13] = {0, 31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
    bool isr(int y) {
        return (y % 400 == 0) || (y % 100 != 0 && y % 4 == 0);
    }
    void normalize() {
        while (day > (isr(year) ? mr[month] : mp[month])) {
            day -= (isr(year) ? mr[month] : mp[month]);
            month++;
            if (month > 12) {
                month = 1;
                year++;
            }
        }
        while (day < 1) {
            month--;
            if (month < 1) {
                month = 12;
                year--;
            }
            day += (isr(year) ? mr[month] : mp[month]);
        }
    }
    bool operator> (Date d) {
        if (year != d.year) {
            return year > d.year;
        }
        if (month != d.month) {
            return month > d.month;
        }
        return day > d.day;
    }
};
