### c++.CSTRING 类的设计

```c++
#include<iostream>
#include<cstring>
using namespace std;

/* 你提交的代码在这里 */
class CSTRING {
public:
     char* p = nullptr;
     ~CSTRING() {
        if (p != nullptr) {
            delete[] p;
        }
     }
     CSTRING() {
        p = nullptr;
     }
     CSTRING(const CSTRING& other) {
        p = new char[strlen(other.p)+1];
        strcpy(p, other.p);
     }
     const char* GetData() const{
        return p ? p : "";
     }
     void Set (const char* s) {
        if (p != nullptr) {
            delete[] p;
        }
        p = new char[strlen(s) + 1];
        strcpy(p, s);
     }
     void Show() const{
        cout << p << endl;
     }
     CSTRING& operator= (const char* s) {
        if (p != nullptr) {
            delete[] p;
        }
        p = new char[strlen(s) + 1];
        strcpy(p, s);
        return *this;
     }

     CSTRING& operator= (const CSTRING& c) {
        if (p != nullptr) {
            delete[] p;
        }
        p = new char[strlen(c.p) + 1];
        strcpy(p, c.p);
        return *this;
     }
     CSTRING& operator+= (const CSTRING& c) {
        const char* tt = p ? p : "";
        char* tmp = new char[strlen(c.p) + strlen(tt) + 1];
        strcpy(tmp, tt);
        strcat(tmp, c.p);
        if (p != nullptr) {
            delete[] p;
        }
        p = tmp;
        return *this;
     }
     bool operator> (const CSTRING& c) {
        return strcmp(this->p, c.p) > 0;
     }
     CSTRING operator+ (const CSTRING& c) {
        CSTRING tmp;
        if (c.p == nullptr) {
            return *this;
        }
        tmp.p = new char[strlen(c.p) + strlen(p) + 1];
        strcpy(tmp.p, p);
        strcat(tmp.p, c.p);
        return tmp;
     }
     char& operator[] (int i) {
        return p[i];
     }
};
istream& operator>> (istream& cin, CSTRING& c) {
    string tmp;
    cin >> tmp;
    if (c.p != nullptr) {
        delete[] c.p;
    }
    c.p = new char[tmp.length() + 1];
    strcpy(c.p, tmp.c_str());
    return cin;
}
ostream& operator<< (ostream& cout, CSTRING& c) {
    cout << (c.p ? c.p : "");
    return cout;
}

int main()
{
    CSTRING t;
    cout<<"Length of string:"<<strlen(t.GetData())<<endl;
    t.Set("This is an example");
    t.Show();
    cout<<"Length of string:"<<strlen(t.GetData())<<endl;
    t="Sample";
    cout<<t<<endl;
    int n,i;
    cin>>n;
    CSTRING s,*cs;
    cs=new CSTRING[n];
    for(i=0;i<n;i++)
    {
        cin>>cs[i];
        s+=cs[i];
    }
    for(i=0;i<n;i++)
        cout<<"("<<i+1<<") "<<cs[i]<<endl;
    cout<<s<<endl;

    int j;
    for(i=0;i<n-1;i++)
        for(j=0;j<n-i-1;j++)
            if(cs[j]>cs[j+1])
            {  t=cs[j];   cs[j]=cs[j+1];   cs[j+1]=t;   }

    for(i=0;i<n;i++)
        cout<<"("<<i+1<<") "<<cs[i]<<endl;
    t[0]='\0';

    for(i=0;i<n;i++)
    {
        t=cs[i]+t;
    }
    cout<<t<<endl;
    delete[] cs;
    return 0;
}
